import { render } from '@testing-library/react';
import React from 'react';
// import shrekImg from '../assets/shrek.png';

const Todos = () => {
  const state = {
    filmes: [
      {
        id: 0,
        title: 'Shrek',
        paragraph:
          'Para resgatar uma princesa das garras de um dragão que cospe fogo, o ogro Shrek se une a um companheiro improvável: um burro brincalhão.',
        img: 'https://media.graphassets.com/rHzKc7MuSFG3Keyg6IuC',
      },
    ],
    listFilms: [],
  };
  render();
  return (
    <div>
      {this.state.Films.map((item) => (
        <Box>
          <Poster src={item.img} alt={item.title} onClick={this.OpenCard} />
          <h2>{item.title}</h2>
          <p>{item.paragraph}</p>
        </Box>
      ))}
    </div>
  );
};
export default Todos;
